package java_놀이;

public class day01_두수비교하기 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 2;
		int b = 1;
		
		if(a > b) {
			System.out.println(">");
		}
		
		a = 10; b = 2;
		
		if(a > b) {
			System.out.println(">");
		}
		
		a = 5; b = 5;
		if(a == b) {
			System.out.println("==");
		}
	}
}
